using System.Collections.Generic;
using UnityEngine;

/// <summary>
/// 포션들의 매치 상태를 나타내는 열거형
/// </summary>
public enum MatchDirection
{
    Horizontal, //가로 3
    Vertical, //세로 3
    LongVertical, //세로 4
    LongHorizontal, //가로 4
    Super, //5개 이상
    None, //없음
}

/// <summary>
/// 포션 매치 결과를 나타내는 클래스
/// </summary>
public class MatchResult
{
    public List<Piece> connectedPotions;
    public MatchDirection direction;
}

using System.Collections.Generic;

/// <summary>
/// 피스들의 매치 상태를 나타내는 열거형
/// </summary>
public enum MatchDirection
{
    Horizontal, //가로 3
    Vertical, //세로 3
    LongVertical, //세로 4
    LongHorizontal, //가로 4
    Super, //5개 이상
    None, //없음
}

/// <summary>
/// 피스 매치 결과를 나타내는 클래스
/// </summary>
public class MatchResult
{
    public List<Piece> connectedPieces;
    public MatchDirection direction;
}

using UnityEngine;

public class Node : MonoBehaviour
{
    public bool isUsable; //공간을 물약으로 채울 수 있는지 여부를 결정합니다.

    public GameObject piece; //Node가 현재 가지고 있는 포션 오브젝트

    //생성자
    // public Node(bool isUsable, GameObject piece)
    // {
    //     this.isUsable = isUsable;
    //     this.piece = piece;
    // }

    // 추가: 셀 좌표
    public int x;
    public int y;

    public bool IsEmpty => piece == null; // 변수명은 어디까지나 소문자로 시작할 것 아니면 걍 함수로 바꾸셈
    // public bool IsEmpty() => piece == null;

    // 칸 초기화(생성자 대신 사용)
    public void Initialize(int kX, int kY, bool kUsable = true) // 파라미터 이름 바꿔놈 앞에 접두사 붙여서 구분하셈 this 보다는
    {
        x = kX;
        y = kY;
        isUsable = kUsable;
    }

    // 칸에 포션 배치 + 좌표 동기화
    public void SetPiece(GameObject go)
    {
        piece = go;
        var p = go != null ? go.GetComponent<Piece>() : null;
        if (p != null)
        {
            p.SetIndices(x, y); // 기존 SetIndices 호출 보완
        }
    }

    public void Clear() => piece = null;
}

